package Lec_1;

public class Arithmetic_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int a = 9;
         int b = 7;
         int c = a + b;
         System.out.println(c);
          c  = a - b;
         System.out.println(c);
         int x = 2;
         int y = 4;
         int z = x*y;
         System.out.println(z);
         int p = 110;
         int q = 4;
         int r = p/q;
         System.out.println(r);
         int m = 89;
         int n = 7;
         int o = m%n;
         System.out.println(o);
	}

}
