package Lec_1;

public class Assignment_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int x  = 5;
         x += 10;
         System.out.println(x);
         x -= 5;
         System.out.println(x);
         x *= 6;
         System.out.println(x);
         x /= 7;
         System.out.println(x);
         x %= 3;
         System.out.println(x);
	}

}
