package Lec_1;

public class Maximum_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           int a = 7;
           int b = 5;
           int c = 11;
           
           if(a > b && a > c) { 
        	   System.out.println("a is maximum");
           }
           else if(b > a && b > c) {
        	   System.out.println("b is maximum");
           }
           else{
        	    System.out.println("c is maximum");
           }
	}

}
